import React, {useState} from 'react';
// Search bar for homepage

 function Searchbar(){

    const [filteredData, setFilteredData] = useState(null)

    //${encodeURIComponent(searchInput)}
    const searchHandler = (event) => {
        const searchInput = event.target.value
        const params = {
            api_key: 'QRfBMF4C6vGHhpSERkdxZWSqgQXlpkG1SeM86v48',
            query: searchInput,
            dataType: ["Survey (FNDDS)"],
            pageSize: 10,
        }
    
        const api_url = `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=${encodeURIComponent(params.api_key)}&query=${encodeURIComponent(params.query)}&dataType=${encodeURIComponent(params.dataType)}&pageSize=${encodeURIComponent(params.pageSize)}`;
        
        function getData() {
            return fetch(api_url).then(response => response.json())
        }
        
        var data = getData();
        data.then(function(result) {
            setFilteredData(result)
        });
        console.log(filteredData)
        // fetch(`http://localhost:9000/api/chicken`)
        // .then((response) => {
        //     response.json()
        //     console.log(response)
        // })
        // .then(setFilteredData)
        // console.log(filteredData)
    }

    return (
       <div className="searchBar">
            <div className="searchInput">
                <input type="text" placeholder='Enter a food' onChange={searchHandler}/>
                <div className='searchIcon'></div>
            </div>
            <div className="data">
                
            </div>
         </div>
    )
}

export default Searchbar;
